import React, { useState, useEffect } from 'react';

const JobsTab = () => {
  const [jobs, setJobs] = useState([]);
  const [categories, setCategories] = useState([]);
  const [locations, setLocations] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('');
  const [selectedLocation, setSelectedLocation] = useState('');

  useEffect(() => {
    fetchCategories();
    fetchLocations();
  }, []);

  const fetchCategories = async () => {
    try {
      const response = await fetch('https://api.adzuna.com/v1/api/jobs/categories?app_id=c517dc0d&app_key=f77d04712010a1dc057e4feeead4adc4');
      if (!response.ok) {
        throw new Error('Failed to fetch categories');
      }
      const data = await response.json();
      setCategories(data.results);
    } catch (error) {
      console.error('Error fetching categories:', error);
    }
  };



  const handleDone = () => {
    fetchJobs();
  };

  const fetchJobs = async () => {
    try {
      const response = await fetch(`https://api.adzuna.com/v1/api/jobs/us/search/1?app_id=c517dc0d&app_key=f77d04712010a1dc057e4feeead4adc4&category=${selectedCategory}&location=${selectedLocation}`);
      if (!response.ok) {
        throw new Error('Failed to fetch job listings');
      }
      const data = await response.json();
      setJobs(data.results);
    } catch (error) {
      console.error('Error fetching jobs:', error);
    }
  };

  return (
    <div>
      <h2>Job Listings</h2>
      <div>
        <label htmlFor="category">Category:</label>
        <select id="category" value={selectedCategory} onChange={(e) => setSelectedCategory(e.target.value)}>
          <option value="">Select a category</option>
          {categories.map((category, index) => (
            <option key={index} value={category.code}>{category.label}</option>
          ))}
        </select>
      </div>
      <div>
        <label htmlFor="location">Location:</label>
        <select id="location" value={selectedLocation} onChange={(e) => setSelectedLocation(e.target.value)}>
          <option value="">Select a location</option>
          {locations.map((location, index) => (
            <option key={index} value={location.code}>{location.label}</option>
          ))}
        </select>
      </div>
      <button onClick={handleDone}>Done</button>
      <ul>
        {jobs.map((job, index) => (
          <li key={index}>
            <h3>{job.title}</h3>
            <p>{job.description}</p>
            <p>{job.company.display_name}</p>
            {/* Add more job details as needed */}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default JobsTab;
