import React, { useState, useEffect } from 'react';

const App = () => {
  const [categories, setCategories] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('');
  const [jobs, setJobs] = useState([]);

  useEffect(() => {
    fetchCategories();
  }, []);

  const fetchCategories = async () => {
    try {
      const response = await fetch('http://api.adzuna.com/v1/api/jobs/gb/categories?app_id=c517dc0d&app_key=f77d04712010a1dc057e4feeead4adc4&&content-type=application/json');
      if (!response.ok) {
        throw new Error('Failed to fetch job categories');
      }
      const data = await response.json();
      setCategories(data.results);
    } catch (error) {
      console.error('Error fetching job categories:', error);
    }
  };

  const fetchJobs = async () => {
    try {
      const response = await fetch(`http://api.adzuna.com/v1/api/jobs/gb/search/1?app_id=c517dc0d&app_key=f77d04712010a1dc057e4feeead4adc4&category=${selectedCategory}&content-type=application/json`);
      if (!response.ok) {
        throw new Error('Failed to fetch jobs');
      }
      const data = await response.json();
      setJobs(data.results);
    } catch (error) {
      console.error('Error fetching jobs:', error);
    }
  };

  const handleCategoryChange = (e) => {
    setSelectedCategory(e.target.value);
  };

  const handleSearch = () => {
    if (selectedCategory) {
      fetchJobs();
    }
  };

  return (
    <div>
      <h1>Job Categories</h1>
      <select onChange={handleCategoryChange}>
        <option value="">Select a category</option>
        {categories.map((category, index) => (
          <option key={index} value={category.code}>{category.label}</option>
        ))}
      </select>
      <button onClick={handleSearch}>Search</button>
      <h2>Jobs</h2>
      <ul>
        {jobs.map((job, index) => (
          <li key={index}>{job.title}</li>
        ))}
      </ul>
    </div>
  );
};

export default App;
