import React, { useState, useEffect } from 'react';

const JobsTab = () => {
  const [jobs, setJobs] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('');
  const [categories, setCategories] = useState([]);

  useEffect(() => {
    fetchCategories();
  }, []);

  useEffect(() => {
    if (selectedCategory) {
      fetchJobs(selectedCategory);
    }
  }, [selectedCategory]);

  const fetchCategories = async () => {
    try {
      const response = await fetch('http://api.adzuna.com/v1/api/jobs/gb/categories?app_id=c517dc0d&app_key=f77d04712010a1dc057e4feeead4adc4&&content-type=application/json');
      if (!response.ok) {
        throw new Error('Failed to fetch job categories');
      }
      const data = await response.json();
      setCategories(data.results);
    } catch (error) {
      console.error('Error fetching job categories:', error);
    }
  };

  const fetchJobs = async (category) => {
    try {
      const response = await fetch(`https://api.adzuna.com/v1/api/jobs/gb/search/1?app_id=c517dc0d&app_key=f77d04712010a1dc057e4feeead4adc4&category=${category}`);
      if (!response.ok) {
        throw new Error('Failed to fetch job listings');
      }
      const data = await response.json();
      setJobs(data.results);
    } catch (error) {
      console.error('Error fetching jobs:', error);
    }
  };

  const handleCategoryChange = (event) => {
    const selectedCategory = event.target.value;
    console.log('Selected category:', selectedCategory);
    setSelectedCategory(selectedCategory);
  };

  return (
    <div>
      <h2>Job Listings</h2>
      <select value={selectedCategory} onChange={handleCategoryChange}>
        <option value="">Select a category</option>
        {categories.map((category, index) => (
          <option key={index} value={category.code}>{category.label}</option>
        ))}
      </select>
      <ul>
        {jobs.map((job, index) => (
          <li key={index}>
            <h3>{job.title}</h3>
            <p>{job.description}</p>
            <p>{job.company.display_name}</p>
            {/* Add more job details as needed */}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default JobsTab;
