import React, { useState, useEffect } from 'react';

const JobsTab = () => {
  const [jobsByCategory, setJobsByCategory] = useState({});

  useEffect(() => {
    fetchJobs();
  }, []);

  const fetchJobs = async () => {
    try {
      const categories = ['Engineering', 'Healthcare & Nursing', 'IT Jobs']; // Add more categories as needed
      const jobsByCategory = {};

      for (const category of categories) {
        const response = await fetch(`https://api.adzuna.com/v1/api/jobs/gb/search/1?app_id=c517dc0d&app_key=f77d04712010a1dc057e4feeead4adc4&category=${encodeURIComponent(category)}`);
        if (!response.ok) {
          throw new Error(`Failed to fetch job listings for category: ${category}`);
        }
        const data = await response.json();
        jobsByCategory[category] = data.results;
      }

      setJobsByCategory(jobsByCategory);
    } catch (error) {
      console.error('Error fetching jobs:', error);
    }
  };

  return (
    <div>
      <h2>Job Listings</h2>
      {Object.entries(jobsByCategory).map(([category, jobs]) => (
        <div key={category}>
          <h3>{category}</h3>
          <ul>
            {jobs.map((job, index) => (
              <li key={index}>
                <h4>{job.title}</h4>
                <p>{job.description}</p>
                <p>{job.company.display_name}</p>
                {/* Add more job details as needed */}
              </li>
            ))}
          </ul>
        </div>
      ))}
    </div>
  );
};

export default JobsTab;
