import React, { useState, useEffect } from 'react';

const JobsTab = () => {
  const [jobs, setJobs] = useState([]);
  const [categories, setCategories] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('');

  useEffect(() => {
    fetchCategories();
    fetchJobs(selectedCategory); // Fetch jobs initially for the selected category
  }, [selectedCategory]); // Fetch jobs whenever the selected category changes

  const fetchCategories = async () => {
    try {
      const response = await fetch('https://api.adzuna.com/v1/api/jobs/gb/categories?app_id=c517dc0d&app_key=f77d04712010a1dc057e4feeead4adc4');
      if (!response.ok) {
        throw new Error('Failed to fetch job categories');
      }
      const data = await response.json();
      setCategories(data.results);
    } catch (error) {
      console.error('Error fetching job categories:', error);
    }
  };

  const fetchJobs = async (category = '') => {
    try {
      const response = await fetch(`https://api.adzuna.com/v1/api/jobs/us/search/1?app_id=c517dc0d&app_key=f77d04712010a1dc057e4feeead4adc4&category=${encodeURIComponent(category)}`);
      if (!response.ok) {
        throw new Error('Failed to fetch job listings');
      }
      const data = await response.json();
      setJobs(data.results);
    } catch (error) {
      console.error('Error fetching jobs:', error);
    }
  };

  const handleCategoryClick = (category) => {
    setSelectedCategory(category.label); // Update the selected category in state
  };

  return (
    <div>
      <h2>Job Categories</h2>
      <div>
        {categories.map((category, index) => (
          <button key={index} onClick={() => handleCategoryClick(category)}>
            {category.label}
          </button>
        ))}
      </div>
      <h2>Job Listings</h2>
      <ul>
        {jobs.map((job, index) => (
          <li key={index}>
            <h3>{job.title}</h3>
            <p>{job.description}</p>
            <p>{job.company.display_name}</p>
            {/* Add more job details as needed */}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default JobsTab;
