import React, { useState, useEffect } from 'react';
import './JobInternshipComponent.css';

const JobsTab = () => {
  const [jobs, setJobs] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);

  useEffect(() => {
    fetchJobs();
  }, [currentPage]);

  const fetchJobs = async () => {
    try {
      const response = await fetch(`https://api.adzuna.com/v1/api/jobs/us/search?app_id=c517dc0d&app_key=f77d04712010a1dc057e4feeead4adc4&results_per_page=18&page=${currentPage}`);
      if (!response.ok) {
        throw new Error('Failed to fetch job listings');
      }
      const data = await response.json();
      setJobs((prevJobs) => [...prevJobs, ...data.results]);
      setTotalPages(Math.ceil(data.count / 18)); // Round up to the nearest integer for total pages
    } catch (error) {
      console.error('Error fetching jobs:', error);
    }
  };

  const handleSeeMore = () => {
    if (currentPage < totalPages && jobs.length < 200) {
      setCurrentPage((prevPage) => prevPage + 1);
    }
  };

  return (
    <div className="job-listings">
      <h2>Job Listings</h2>
      <div className="job-cards">
        {jobs.map((job, index) => (
          <div key={index} className="job-card">
            <h3><a href={job.redirect_url} target="_blank" rel="noopener noreferrer">{job.title}</a></h3>
            <p>{job.description}</p>
            <p className="company">{job.company.display_name}</p>
            {/* Add more job details as needed */}
          </div>
        ))}
      </div>
      {currentPage < totalPages && jobs.length < 200 && (
        <button onClick={handleSeeMore} className="see-more-btn">See More</button>
      )}
    </div>
  );
};

export default JobsTab;
