import React, { useState, useEffect } from 'react';

const JobsTab = () => {
  const [jobs, setJobs] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('');
  const [selectedLocation, setSelectedLocation] = useState('');

  useEffect(() => {
    fetchJobs(selectedCategory, selectedLocation);
  }, [selectedCategory, selectedLocation]);

  const fetchJobs = async (category, location) => {
    try {
      let endpoint = 'https://api.adzuna.com/v1/api/jobs/us/search/1?app_id=c517dc0d&app_key=f77d04712010a1dc057e4feeead4adc4';
      if (category) {
        endpoint += `&category=${encodeURIComponent(category)}`;
      }
      if (location) {
        endpoint += `&location=${encodeURIComponent(location)}`;
      }

      const response = await fetch(endpoint);
      if (!response.ok) {
        throw new Error('Failed to fetch job listings');
      }
      const data = await response.json();
      setJobs(data.results);
    } catch (error) {
      console.error('Error fetching jobs:', error);
    }
  };

  const handleCategoryChange = (event) => {
    setSelectedCategory(event.target.value);
  };

  const handleLocationChange = (event) => {
    setSelectedLocation(event.target.value);
  };

  return (
    <div>
      <h2>Job Listings</h2>
      <div>
        <label htmlFor="category">Select Category: </label>
        <select id="category" value={selectedCategory} onChange={handleCategoryChange}>
          <option value="">All</option>
          <option value="Engineering">Engineering</option>
          <option value="Healthcare & Nursing">Healthcare & Nursing</option>
          {/* Add more categories as needed */}
        </select>
      </div>
      <div>
        <label htmlFor="location">Select Location: </label>
        <select id="location" value={selectedLocation} onChange={handleLocationChange}>
          <option value="">All</option>
          <option value="US">United States</option>
          <option value="GB">United Kingdom</option>
          {/* Add more locations as needed */}
        </select>
      </div>
      <ul>
        {jobs.map((job, index) => (
          <li key={index}>
            <h3>{job.title}</h3>
            <p>{job.description}</p>
            <p>{job.company.display_name}</p>
            {/* Add more job details as needed */}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default JobsTab;
