import React, { useState, useEffect } from 'react';

const JobsTab = () => {
  const [jobs, setJobs] = useState([]);

  useEffect(() => {
    fetchJobs();
  }, []);

  const fetchJobs = async () => {
    try {
      // Specify the categories you want to fetch jobs for
      const categories = ['Engineering', 'Healthcare & Nursing', 'Information Technology'];

      // Fetch jobs for each category
      const jobsPromises = categories.map(async (category) => {
        const response = await fetch(`https://api.adzuna.com/v1/api/jobs/gb/search/1?app_id=c517dc0d&app_key=f77d04712010a1dc057e4feeead4adc4&category=${encodeURIComponent(category)}`);
        if (!response.ok) {
          throw new Error(`Failed to fetch jobs for category: ${category}`);
        }
        const data = await response.json();
        return data.results;
      });

      // Wait for all requests to complete and concatenate the results
      const allJobs = await Promise.all(jobsPromises);
      const allJobsFlattened = allJobs.flat();

      setJobs(allJobsFlattened);
    } catch (error) {
      console.error('Error fetching jobs:', error);
    }
  };

  return (
    <div>
      <h2>Job Listings</h2>
      <ul>
        {jobs.map((job, index) => (
          <li key={index}>
            <h3>{job.title}</h3>
            <p>{job.description}</p>
            <p>{job.company.display_name}</p>
            {/* Add more job details as needed */}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default JobsTab;
